/*
 * Author: jp
 * Date: 2015-09-15
 * Function: 存放全局常量,文件,模块,socket,命令,CONFIG_CONST中的变量不能在运行时修改
 */

(function () {

    //全局常量
    var CONFIG_CONST = 
    {
        //https具体请求path 目前使用的android VDI的路径
        MODULE_PATH     : '/com/PhoneModule.xml?rnd=0.123456',

        //unix socket 路径
        EC_SOCKET       : '/run/adesk/ec/socket',
        AGENT_SOCKET    : '/run/adesk/agent/socket',
        MONITOR_SOCKET  : '/run/adesk/monitor/socket',

        //配置文件的路径
        EC_CONF         : '/etc/adesk/ec.conf',
        AGENT_CONF      : '/etc/adesk/agent.conf',
        VDC_CONF        : '/etc/adesk/vdc.conf',
        USER_CONF       : '/etc/adesk/user.conf',
        SESSION_CONF    : '/run/adesk/session.conf',
        POLICY_CONF     : '/etc/adesk/policy.conf',

        //
        ADESK_CONFIG_PATH : 'adesk-config',
		
		//confXML配置文件路径
		CONF_XML_PATH: '/etc/adesk/confXML.conf',
		
		//spiece路径
		SPICEC_PATH: '/root/spicec',

        //短连接相关配置
        //socket短连接超时 
        SHORT_SOCKET_TIME_OUT : 60000,
        
        //心跳相关配置
        //心跳包间隔时间 
        HERAT_BEAT_TIME : 2000,
        //心跳断开后或者没连上时的重连间隔 
        RE_START_HEART_TIME : 2000,

        //子进程相关配置 
        //子进程超时未响应
        CHILD_PROCESS_TIME_OUT : 30000,

        //日志相关配置
        //在syslog中的标签
        LOG_NAME : 'sangfor_ec',
        //日志等级
        LOG_LEVELS :
        {
            NONE    : 101, //用于标示不输出日志 <-将日志等级设为NONE则不会输出日志
            FATAL   : 50,  //严重错误
            ERROR   : 40,  //普通错误
            WARNING : 30,  //警告
            LOG     : 20,  //运行记录 <-默认情况在在此之上的日志输出到syslog
            DEBUG   : 10   //调试信息　<-默认情况下在此上之上的日志会输出到console
        },

        //syslog
        LOG_MAPPINGS :
        {
            //         LOG_EMERG
            FATAL   : 'LOG_ALERT',
            //         LOG_CRIT
            ERROR   : 'LOG_ERR',       
            WARN    : 'LOG_WARNING',  
            //         LOG_NOTICE
            LOG     : 'LOG_INFO',                                        
            DEBUG   : 'LOG_DEBUG'   
        },
        //默认大于等于此等级的日志会输出到syslog
        DEFAULT_SYS_LOG_LEVEL : 'LOG',
        //默认大于等于此等级的日志会输出到浏览器debug页面
        DEFAULT_CONSOLE_LOG_LEVEL : 'DEBUG',
    };

    var getval = function(key){
        return CONFIG_CONST[key];
    };

    var valin = function(key){
        return (key in CONFIG_CONST);
    }
        
    exports.getval = getval;
    exports.valin  = valin;

})()