//基本文件读写模块

(function () {

    var fs = require('fs');    

    /*
    * @method readFile 读取文件
    * @param path {string}  文件路径
    * @param callbackSuccess {function}  ( data {string} ) 
    * @param callbackFaile {function} ( err {object} ) 
    */
    var readFile = function(path, callbackSuccess, callbackFaile){
        fs.readFile(path, 'utf-8', function(err, data){
            if(err){
                callbackFaile(err);
            }else{
                callbackSuccess(data);
            }            
        });
    };

    /*
    * @method readFile 写文件
    * @param path {string}  文件路径
    * @param callbackSuccess {function} () 
    * @param callbackFaile {function} (err {object} ) 
    */
    var writeFile = function (path, file, callbackSuccess, callbackFaile) {
        fs.writeFile(path, file, function(err){
            if(err){
                callbackFaile(error);
            }else{
                callbackSuccess();
            }
        });
    }

    exports.readFile = readFile;
    exports.writeFile = writeFile;
})()