//全局变量模块

(function () {

    //供读写的全局变量
    var globalval = {
        //登录流程
        "TWFID":null,                      //session_id
        "VDIADDR":null,                   //选路后的URL地址
        "port":null,                    //登录端口
        "ENABLE_RANDCODE":"0",         //验证码，服务端通过cookie下发的字段值
        "enablerandcode":"0",         //维护一个是否启用校验码，预登录获取的xml的RndImg字段值        
        "userdata":null,             //user.conf字段值，见总设，是对象，登录时候会保存
        "enableautologin":null,    //服务端下发是否允许自动登录开关，正常登录一次之后，不需要维护
        "enablesavepwd":null,     //服务端下发是否允许记住密码开关，正常登录一次之后，不需要维护
        "isautologin":"0",       //是否需要自动登录，多重条件的结果，搜索login.js查看
        "preloginfail":false,   //预登录失败则重走一遍，用于异常
        "pwp_errorcode":-1,    //密码策略错误码
        //任务栏
        "ethstatus": "ok",                  // eth0网口状态，ok or no 网线插入和未插入
        "vdcconnect": "ok",                // vdc连接断开状态 ok or no 登录后是否掉线，ok未掉线，no掉线
        "issupportwifi":"0",              //是否支持wifi功能 0 or 1
        "usewifi": "0",                  //wifi是否启用，默认关闭 0 or 1
        "wifidata":null,                //wifi数据
        "islogin": "no",               // 是否登录状态，ok or no
        "iseditlogininfo":"0",         //修改登录信息需要密码标记 0 or 1
        "iseditsysteminfo":"0",         //修改设置需要密码 0 or 1
        "pwdforsetlogininfo":null,     //修改登录信息需要的密码和修改设置需要的密码MD5值
        "logintime": "-",             // 登录时间，"-" or time
        "typewrite":'cn',            // 输入法，'cn' or 'us'
        "systemtime":'-',           //系统时间，未定义
        "sysvol": 100,             //系统音量，默认值100，会同步独享桌面音量
        "sysvol_forbidden": "no", //音量是否禁用
        "updatestatus":'no',     //是否有版本更新，未定义
        "vdcconf":null,         //vdc.conf配置文件，对象
        "network":null,        //网络参数
        //退出流程
        "spicecpid":"-1"  //打开spicec获取的对象
    };

    var setval = function(key, val){
        globalval[key] = val;
    };

    var getval = function(key){
        return globalval[key];
    };

    var valin = function(key){
        return (key in globalval);
    };
    var getall = function(){
        return globalval;
    };
    exports.setval = setval;
    exports.getval = getval;
    exports.valin  = valin;
    exports.getall = getall;
})()